import React, {Component} from 'react';
import './App.css';

class App extends Component{
  constructor(){
    super()
    this.state = {
      city:"",
      weather:"",
      wind:"",
    }
  this.getData = this.getData.bind(this)
  }

  getData() {
    fetch('api.openweathermap.org/data/2.5/weather?q=Lisbon&appid=4c77efd9f8291e2531b89885b49ca00e')
      .then(function(response){
        return response.json()
      })
      .then(function(response){
        this.setState({city:response.main.temp})
      })
  }

  render(){
    return(
      <div>
        <button onClick={this.getData}>click</button>
        <h1>{this.state.city}</h1>
      </div>

    )
  }

}

export default App;
