  this.getData = this.getData.bind(this)
  }

  getData() {
    fetch('api.openweathermap.org/data/2.5/weather?q=Lisbon&appid=4c77efd9f8291e2531b89885b49ca00e',{mode : 'cors'})
      .then(function(response){
