import React, {Component} from 'react';
import './App.css';

class App extends Component{
  constructor(){
    super()
    this.state = {
      city:"Lisbon",
      cityData:"",
      invisible: true,
      isFetching : true,
    }
  this.getData = this.getData.bind(this)
  this.handleChange = this.handleChange.bind(this)
  this.toggleClass = this.toggleClass.bind(this)
  }

  getData() {
    const that = this;
    fetch('http://api.openweathermap.org/data/2.5/weather?q='+that.state.city+'&appid=4c77efd9f8291e2531b89885b49ca00e',{mode : 'cors'})
      .then(function(response){
        return response.json()
      })
      .then(function(response){
        that.setState({cityData:response})
        that.setState({isFetching: false})
        console.log("fez fetch")
      })

      if(this.state.invisible === true){
        that.toggleClass()
      }

  }

  handleChange(event){
    this.setState({city: event.target.value})
  }

  toggleClass(){
    const currentState = this.state.invisible;
    this.setState({invisible: !currentState});
}

componentDidMount() {
  this.getData();
}

  render(){

    return(
      <div>
        <input 
        type="text"
        value={this.state.city}
        name="city"
        placeholder="Insert City Name"
        onChange={this.handleChange
        } />

        <button onClick={this.getData}>Search</button>

        {this.state.isFetching? null : (
          <div className={this.state.invisible ? "invisible" : null}>

            <div className = "topContainer">
              <h2>{this.state.cityData.name}</h2>
              <h4>Hours:</h4>
            </div>

              <div> 
                <h4>Temperature :{this.state.cityData.main.temp}</h4>
                <h4>Feels Like :{this.state.cityData.main.feels_like}</h4>
                <h1>Description: {this.state.cityData.weather[0].description}</h1>
              </div>

              <div>
                <img alt="icon" src={"http://openweathermap.org/img/w/"+ this.state.cityData.weather[0].icon + ".png" }/>
                <h4>min</h4>
                <h4>max</h4>
              </div>
            <div className = "middleContainer"> </div>

            <div className = "bottomContainer">
              5day rpediction
            </div>







          </div>
        )}



      </div>

    )
  }

}

export default App;
