import React, {Component} from 'react';
import './App.css';

class App extends Component{
  constructor(){
    super()
    this.state = {
      city:"Lisbon",
      currentCityWeather:"",
      predictionWeather:"",
      invisible: true,
      isFetching : true,
    }
  this.getData = this.getData.bind(this)
  this.handleChange = this.handleChange.bind(this)
  this.toggleClass = this.toggleClass.bind(this)
  }

  getData() {
    const that = this;
    Promise.all([
      fetch('http://api.openweathermap.org/data/2.5/weather?q='+that.state.city+'&appid=4c77efd9f8291e2531b89885b49ca00e&units=metric',{mode : 'cors'}),
      fetch('http://api.openweathermap.org/data/2.5/forecast?q='+that.state.city+'&appid=4c77efd9f8291e2531b89885b49ca00e&units=metric',{mode : 'cors'})
    ])
      .then(async([current, forecast]) => {
        const currentWeather = await current.json();
        const forecastWather = await forecast.json();
        return [currentWeather, forecastWather]
      })

      .then(function(response){
        console.log(response[0])
        that.setState({currentCityWeather: response[0]})
        that.setState({predictionWeather: response[1]})
        that.setState({isFetching: false})
      })
      .catch(function(error){
        console.log(error)
      })

      if(this.state.invisible === true){
        that.toggleClass()
      }

  }

  // async getData() {
  //   try{
  //     let [currentData, fiveDayData] = await Promise.all([
  //       fetch('http://api.openweathermap.org/data/2.5/weather?q='+this.state.city+'&appid=4c77efd9f8291e2531b89885b49ca00e&units=metric',{mode : 'cors'}),
  //       fetch('http://api.openweathermap.org/data/2.5/forecast?q='+this.state.city+'&appid=4c77efd9f8291e2531b89885b49ca00e&units=metric',{mode : 'cors'})
  //     ]);

  //     this.state.currentCityWeather = await currentData.json()
  //     this.state.predictionWeather = await fiveDayData.json()


  //   }catch(err){
  //     console.log(err)
  //   }
  // }


  handleChange(event){
    this.setState({city: event.target.value})
  }

  toggleClass(){
    const currentState = this.state.invisible;
    this.setState({invisible: !currentState});
}

componentDidMount() {
  this.getData();
}

  render(){

    return(
      <div>
        <input 
        type="text"
        value={this.state.city}
        name="city"
        placeholder="Insert City Name"
        onChange={this.handleChange
        } />

        <button onClick={this.getData}>Search</button>

        {this.state.isFetching? null : (
          <div className={this.state.invisible ? "invisible" : null}>

            <div className = "topContainer">
              <h2>{this.state.currentCityWeather.name}</h2>
              <h4>Hours:</h4>
            </div>

              <div> 
                <h4>Temperature :{this.state.currentCityWeather.main.temp}</h4>
                <h4>Feels Like :{this.state.currentCityWeather.main.feels_like}</h4>
                <h1>Description: {this.state.currentCityWeather.weather[0].description}</h1>
              </div>

              <div>
                <img alt="icon" src={"http://openweathermap.org/img/w/"+ this.state.currentCityWeather.weather[0].icon + ".png" }/>
                <h4>Min: {this.state.currentCityWeather.main.temp_min}</h4>
                <h4>Max: {this.state.currentCityWeather.main.temp_max}</h4>
              </div>
            <div className = "middleContainer"> </div>

            <div className = "bottomContainer">
              5day rpediction
            </div>
          </div>
        )}



      </div>

    )
  }

}

export default App;
