import React, {Component} from 'react';
import './App.css';

class App extends Component{
  constructor(){
    super()
    this.state = {
      city:"",
      cityData:"",
      invisible: true
    }
  this.getData = this.getData.bind(this)
  this.handleChange = this.handleChange.bind(this)
  }

  getData() {
    const that = this;
    fetch('http://api.openweathermap.org/data/2.5/weather?q='+that.state.city+'&appid=4c77efd9f8291e2531b89885b49ca00e',{mode : 'cors'})
      .then(function(response){
        return response.json()
      })
      .then(function(response){
        that.setState({cityData:response})
      })
  }

  handleChange(event){
    this.setState({city: event.target.value})
  }

  render(){
    return(
      <div>
        <input 
        type="text"
        value={this.state.city}
        name="city"
        placeholder="Insert City Name"
        onChange={this.handleChange
        } />

        <button onClick={this.getData}>Search</button>
        <div className = "main-display">
          <h1>{this.state.cityData === "" ? "Search for your city" : this.state.cityData.name}</h1>
        </div>


      </div>

    )
  }

}

export default App;
