import React, {Component} from 'react';
import './App.css';

class App extends Component{
  constructor(){
    super()
    this.state = {
      city:"",
      weather:"",
      wind:"",
    }

  }

  getData() {
    fetch('api.openweathermap.org/data/2.5/weather?q=Lisbon&appid=4c77efd9f8291e2531b89885b49ca00e')
      .then(function(response){
        return response.json()
      })
      .then(function(response){
        this.setState({city:response.main.temp})
      })
  }

  render(){
    return(
      <h1>Hello</h1>
    )
  }

}

export default App;
